use leptos::html::Dialog;
use leptos::*;
use wasm_bindgen::JsCast;
use web_sys::HtmlElement;

use tailwind_fuse::*;

use crate::registry::ui::button::{Button, ButtonVariant};

#[component]
pub fn DemoDialog() -> impl IntoView {
    let dialog_ref = create_node_ref::<Dialog>();

    let show_dialog = move |_| {
        if let Some(dialog) = dialog_ref.get() {
            dialog.show_modal().unwrap();
        }
    };

    let close_dialog = move |_| {
        if let Some(dialog) = dialog_ref.get() {
            dialog.close();
        }
    };

    view! {
        <div>
            <Button on:click=show_dialog variant=ButtonVariant::Outline>
                "Change payment method"
            </Button>

            <dialog
                _ref=dialog_ref
                on:click=move |ev| {
                    let target = ev.target().unwrap();
                    if let Some(element) = target.dyn_ref::<HtmlElement>() {
                        if element.tag_name() == "DIALOG" {
                            if let Some(dialog) = dialog_ref.get() {
                                dialog.close();
                            }
                        }
                    }
                }
                on:close=move |ev| {
                    let target = ev.target().unwrap();
                }
                class="block inset-0 p-0 w-2/3 rounded-2xl opacity-0 duration-300 translate-y-20 text-md transition-[opacity,transform] backdrop:backdrop-blur-sm [&:not([open])]:pointer-events-none [&[open]]:translate-y-0 [&[open]]:opacity-100"
            >
                <form method="dialog">
                    <header class="relative px-8 pt-6 bg-white rounded-t-2xl">
                        <h1 class="text-2xl font-bold">"Change your payment method"</h1>
                        <button
                            type="button"
                            on:click=close_dialog
                            class="flex absolute top-4 right-4 justify-center items-center p-3 w-8 h-8 text-xl bg-gray-100 rounded-md"
                        >
                            <span class="sr-only">"close"</span>
                            "&times;"
                        </button>
                    </header>
                    <main class="py-16 px-8 space-y-3 bg-white">
                        <div class="flex items-center">
                            <label class="mr-auto w-1/3 text-gray-400">"Card number"</label>
                            <input
                                class="py-2 px-3 w-full rounded-lg border"
                                type="text"
                                name="card-number"
                            />
                        </div>

                        <div class="flex items-center">
                            <label class="mr-auto w-1/3 text-gray-400">"Expiration"</label>
                            <input
                                class="py-2 px-3 w-full rounded-lg border"
                                type="text"
                                name="expiration"
                            />
                        </div>

                        <div class="flex items-center">
                            <label class="mr-auto w-1/3 text-gray-400">"CVC"</label>
                            <input
                                class="py-2 px-3 w-full rounded-lg border"
                                type="password"
                                name="cvc"
                                placeholder="•••"
                            />
                        </div>
                    </main>

                    <DialogFooter>
                        <button class="text-gray-400" formmethod="dialog" value="cancel">
                            "Cancel"
                        </button>
                        <button
                            class="py-3 px-5 text-white bg-blue-500 rounded-xl shadow-md transition-colors hover:bg-blue-600"
                            formmethod="dialog"
                            value="submit"
                        >
                            "Save changes"
                        </button>
                    </DialogFooter>
                </form>
            </dialog>
        </div>
    }
}

