#[component]
pub fn DialogFooter(
    #[prop(into, optional)] class: MaybeSignal<String>,
    #[prop(attrs)] attributes: Vec<(&'static str, Attribute)>,
    children: Children,
) -> impl IntoView {
    let class = create_memo(move |_| {
        tw_merge!(
            "flex gap-6 justify-end py-4 px-8 bg-gray-100 rounded-b-2xl",
            class()
        )
    });

    view! {
        <footer {..attributes} class=class>
            {children()}
        </footer>
    }
}
