use leptos::*;

use crate::{
    components::toaster_custom::{
        toast::{Toast, ToastVariant},
        toast_id::ToastId,
        toaster::Toaster,
        types::Toasts,
    },
    registry::ui::button::Button,
};

#[component]
pub fn DemoToaster() -> impl IntoView {
    view! {
        <Toaster>
            <Demo />
        </Toaster>
    }
}

#[component]
fn Demo() -> impl IntoView {
    let toast_context = expect_context::<Toasts>();

    let create_default_toast = move |_| {
        let toast_id = ToastId::new();
        toast_context.toast(
            view! {
                <Toast
                    toast_id
                    variant=ToastVariant::Default
                    title=view! { "Title" }.into_view()
                    description=Some(view! { "Description" }.into_view())
                />
            },
            Some(toast_id),
            None,
        );
    };

    let create_info_toast = move |_| {
        let toast_id = ToastId::new();
        toast_context.toast(
            view! {
                <Toast
                    toast_id
                    variant=ToastVariant::Destructive
                    title=view! { "Title" }.into_view()
                    description=Some(view! { "Description" }.into_view())
                />
            },
            Some(toast_id),
            None,
        );
    };

    let create_warning_toast = move |_| {
        let toast_id = ToastId::new();
        toast_context.toast(
            view! {
                <Toast
                    toast_id
                    variant=ToastVariant::Warning
                    title=view! { "Title" }.into_view()
                    description=Some(view! { "Description" }.into_view())
                />
            },
            Some(toast_id),
            None,
        );
    };

    let create_success_toast = move |_| {
        let toast_id = ToastId::new();
        toast_context.toast(
            view! {
                <Toast
                    toast_id
                    variant=ToastVariant::Success
                    title=view! { "Title" }.into_view()
                    description=Some(view! { "Description" }.into_view())
                />
            },
            Some(toast_id),
            None,
        );
    };

    view! {
        <div class="pt-8 mx-auto max-w-96">
            <h1 class="text-xl">"Leptos toaster"</h1>

            <div class="flex flex-wrap gap-4">
                <Button on:click=create_default_toast>"Default Toast"</Button>
                <Button on:click=create_info_toast class="bg-destructive">
                    "Destructive Toast"
                </Button>
                <Button on:click=create_warning_toast class="bg-warning">
                    "Warning Toast"
                </Button>
                <Button on:click=create_success_toast class="bg-success">
                    "Success Toast"
                </Button>
            </div>
        </div>
    }
}
